import api from '../../../utils/api'

import { useState, useEffect, useContext } from 'react'
import { useHistory } from 'react-router-dom'

import { Link } from 'react-router-dom'
  
import styles from './Dashboard.module.css'

import RoundedImage from '../../layouts/RoundedImage'

import useFlashMessage from '../../../hooks/useFlashMessage'

function MyProfile() {
    const [user, setUser] = useState({})
    const [token] = useState(localStorage.getItem('token') || '')
    const history = useHistory()

    const { setFlashMessage } = useFlashMessage()


    useEffect(() => {
        api.get('/usuarios/check', {
            headers: {
                Authorization: `Bearer ${JSON.parse(token)}`
            }
        }).then((response) => {
            setUser(response.data)
        })
    }, [token])

    async function removeUser() {
        let msgType ='success'

        const data = await api.delete('/usuarios/exclusao', {
            headers: {
                Authorization: `Bearer ${JSON.parse(token)}`
            }
        }).then((response) => {
            return response.data
        }).catch((err) => {
            msgType = 'error'
            return err.response.data
        })
        setFlashMessage(data.message, msgType)
        localStorage.removeItem('token')
        api.defaults.headers.Authorization = undefined
        history.push('/')
    }


    return (
        <section>
            <div className={styles.user_profile_header}>
                <h1>{`Olá, ${user.nome}!`}</h1>
            </div>
            <div className={styles.user_profile_container}> 
                <div className={styles.user_profile_card}>
                    <div className={styles.user_profile_card_img}>
                    <RoundedImage 
                        src={`${process.env.REACT_APP_API}/images/usuarios/${user.imagem}`} 
                        alt={user.nome}
                        width="px100"
                    />
                    </div>
                    <h3>{user.nome}</h3>
                    <p>
                        <span className="bold">Estado: </span> {user.estado}
                    </p>
                    <p>
                        <span className="bold">Área: </span> {user.area}
                    </p>
                    <p>
                        <span className="bold">Tecnologias: </span> {user.tecnologia}
                    </p>
                </div> 
                <div className={styles.actions}>
                        <button onClick={() => {
                            removeUser()
                        }}
                        >
                            Excluir Perfil
                        </button>
                        <br></br>
                        <button onClick={() => {
                            
                        }}
                        >
                            Editar Perfil
                        </button>
                    </div>
            </div>
        </section>
    )
}

export default MyProfile
