import api from '../../../utils/api'

import { useState, useEffect } from "react"
import { Link } from 'react-router-dom'
  
import styles from './Dashboard.module.css'

function MyProfile() {
    const [user, setUser] = useState({})
    const [token] = useState(localStorage.getItem('token') || '')

    useEffect(() => {
        console.log(token)
        api.get('/usuarios/check', {
            headers: {
                Authorization: `Bearer ${JSON.parse(token)}`
            }
        }).then((response) => {
            setUser(response.data)
            console.log(response.data)
        })
    }, [token])


    return (
        <section>
            <div className={styles.user_profile_header}>
                <h1>{`Olá, ${user.nome}!`}</h1>
            </div>
            <div className={styles.user_profile_container}> 

                    <div className={styles.user_profile_card}>
                        <div style={{backgroundImage: `url(${process.env.REACT_APP_API}/images/usuarios/${user.image})`}} className={styles.user_profile_card_image}>
                        </div>
                        <h3>{user.nome}</h3>
                        <p>
                            <span className="bold">Tec: </span> {user.tecnologia}
                        </p>
                    </div>
                
                
            </div>
        </section>
    )
}

export default MyProfile
